@concat(
    if(greater(activity('lkpCheckTempTableExists').output.count, 0),'DROP TABLE DWH.TMP_DUPLICATE_KEYS;',''),
    'CREATE TABLE DWH.TMP_DUPLICATE_KEYS (KEY_1 INTEGER, KEY_2 INTEGER, FEE_CAT_CD_1 CHAR(2), FEE_CAT_CD_2 CHAR(2), COUNT_NCAN INTEGER, LEG_STATUS CHAR(1), CALC_FEE_CAT_CD CHAR(2), DUPLICATE_RECORD_KEY INTEGER, USE_RECORD_KEY INTEGER); GRANT ALL ON DWH.TMP_DUPLICATE_KEYS TO rajkum25'
)

@concat('MERGE into DWH.',
item(),
' a USING (SELECT DUPLICATE_RECORD_KEY, USE_RECORD_KEY FROM DWH.TMP_DUPLICATE_KEYS WHERE USE_RECORD_KEY IS NOT NULL) b on a.CANDIDATE_BACKGROUND_KEY = b.DUPLICATE_RECORD_KEY WHEN MATCHED THEN UPDATE set a.CANDIDATE_BACKGROUND_KEY = b.USE_RECORD_KEY')
